mapper.select:
SELECT
	SignRecord_ID,
	SignRecord_CreateTime,
	SignRecord_Day,
	SignRecord_Month,
	SignRecord_Year,
	SignRecord_UserID,
	SignRecord_Status,
	SignRecord_Deleted,
	SignRecord_Memo

	FROM wtb_SignRecord

		1=1

			and SignRecord_ID = #{ID}


			and SignRecord_Day = #{Day}


			and SignRecord_Month = #{Month}


			and SignRecord_Year =#{Year}


			and SignRecord_Status = #{Status}


			and SignRecord_Deleted =#{Deleted}


			and SignRecord_UserID =#{UserID}


	order by SignRecord_CreateTime desc

		 limit ${start},${limit}

SELECT
	Count(1)
	FROM wtb_SignRecord

		1=1

			and SignRecord_ID = #{ID}


			and SignRecord_Day = #{Day}


			and SignRecord_Month = #{Month}


			and SignRecord_Year =#{Year}


			and SignRecord_Status = #{Status}


			and SignRecord_Deleted =#{Deleted}


			and SignRecord_UserID =#{UserID}

mapper.delete:
update  wtb_SignRecord set SignRecord_Status=88
		where
			 SignRecord_ID = #{ID}

mapper.update:
update  wtb_SignRecord set SignRecord_Status=1
		where
			 SignRecord_ID = #{ID}
